const App = () => {

  const [courses,setCourses] = useState(null);

   async function fetchData() {
    try{
        let respone = await fetch(apiUrl);
        let output = await respone.json();

        //output ->
        setCourses(output);
    }
    catch(error) {
        TransformStream.error("Network me koi dikkat hai");
    }
   }

   useEffect( () => {
    fetchData();
   },[]);

  return (

     <div>
        <NavBar />

        <Filter filterData = {filterData}/>

        <Cards />
     </div>
  );
};

export default App;
