  const [courses,setCourses] = useState(null);
  const [loading,setLoading] = useState(true);

   async function fetchData() {

    setLoading(true);

    try{
        let respone = await fetch(apiUrl);
        let output = await respone.json();

        //output ->

        setCourses(output);
