        TransformStream.error("Network me koi dikkat hai");
    }

    setLoading(false);

   }

   useEffect( () => {
    fetchData();
   },[]);

  return (

     <div>
        <div>
          <NavBar />
        </div>
        <div>
          <Filter filterData={filterData} />
        </div>
        <div>
          {
            loading ? (<Spinner />) : (<Cards courses= {courses}/>)
          }
        </div>
     </div>
  );
};

export default App;
