import "./Spinner.css";


const Spinner = () => {
    return (
        <div className="flex flex-col items-center space-y-2">
     
            <span class="loader"></span>
            <p className="text-bgDark text-lg font-semibold">Loading....</p>
        </div>
    );
}

export default Spinner;
